import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Game = () => {
  const [gameState, setGameState] = useState(null);
  const [direction, setDirection] = useState('');

  // Start the game and load the initial state
  useEffect(() => {
    axios.get('http://localhost:5000/api/start')
      .then(response => setGameState(response.data))
      .catch(error => console.error(error));
  }, []);

  // Handle keyboard events to update direction
  useEffect(() => {
    const handleKeyDown = (event) => {
      const keyDirectionMap = {
        ArrowUp: 'UP',
        ArrowLeft: 'LEFT',
        ArrowDown: 'DOWN',
        ArrowRight: 'RIGHT',
      };
      if (keyDirectionMap[event.key]) {
        setDirection(keyDirectionMap[event.key]);
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Send direction to backend and update game state
  useEffect(() => {
    if (direction) {
      axios.post('http://localhost:5000/api/update', { direction })
        .then(response => setGameState(response.data))
        .catch(error => console.error(error));
    }
  }, [direction]);

  // Render the game board
  if (!gameState) return <div>Loading...</div>;

  return (
    <div>
      <h1>Snake Game</h1>
      <div className="grid">
        {gameState.snake.map((segment, index) => (
          <div
            key={index}
            style={{
              position: 'absolute',
              top: `${segment.y * 20}px`,
              left: `${segment.x * 20}px`,
              width: '20px',
              height: '20px',
              backgroundColor: 'green'
            }}
          />
        ))}
        <div
          style={{
            position: 'absolute',
            top: `${gameState.food.y * 20}px`,
            left: `${gameState.food.x * 20}px`,
            width: '20px',
            height: '20px',
            backgroundColor: 'red'
          }}
        />
      </div>
      <div>Score: {gameState.score}</div>
      {gameState.gameOver && <div>Game Over</div>}
    </div>
  );
};

export default Game;
